const getUsers = () => {
    const url = 'https://yalantis-react-school-api.yalantis.com/api/task0/users';

    return fetch(url)
        .then(response => {
            if (response.ok){
                return Promise.reject(
                    `${response.status} - ${response.statusText}`
                )
            }
            return response.json();

        })
        .then(data => {
            return data
        })
        .catch(error => {
            console.error('Error: ', error)
        })
}
getUsers()
    .then(data => {
        console.log(data)
    })
