import React from 'react';
import {getUsers} from "../data/data";
import Loader from "../Loader";


import './app.css';

class App extends React.Component{
    state ={
        users: [],
        isLoading: false
    }

    componentDidMount() {

        this.setState({
            isLoading:true
        })

        // console.log(this.state.isLoading)

        getUsers().then(users => {
            this.setState({
                users,
                isLoading:false
            })
        })

    }

    render() {
        const {users, isLoading} = this.state;
        console.log(isLoading)
        // if (users.length === 0){
        //     return <p>No users yet</p>
        // }
        // console.log('IsLoading', isLoading)

        if (isLoading){
            return (
                <Loader/>
            )
        }

        return(
            <div className='container'>
                {
                    (isLoading && <Loader/>)
                }
                <h1>Products</h1>
                {
                    users.map(el => (
                        <p key={el.id}>
                            {
                                el.firstName
                            }
                        </p>
                    ))
                }
            </div>
        )
    }
}
export default App;
