import React from 'react';
import {connect} from "react-redux";
import Loader from "../Loader";
import store, {loadUsers} from "../UsersStore";
import PropTypes from 'prop-types';

import './app.css';
import EmployeeBirthdayList from "../EmployeerBirthdayList";
import Employees from "../Emoloyees/Employees";


function isInArray(id, arr) {
    return arr.some(e => e.id === id);
}

console.log(JSON.parse(localStorage.getItem('redux-store')).activeUsers)
class App extends React.Component {

    state = {
        activeUsers: []
    }

    async componentDidMount() {
        await this.props.loadUsers();
    }

    addActiveUser = (obj) => {

        let userStore = JSON.parse(localStorage.getItem('redux-store'));
        let activeUser = userStore.activeUsers;
        userStore = {...userStore, activeUsers: [...activeUser,obj]}
        localStorage.setItem('redux-store',JSON.stringify(userStore))

        this.setState((activeUsers) => {
            let activeUsersLocalStorage = JSON.parse(localStorage.getItem('redux-store')).activeUsers;
            return{
                activeUsers:activeUsersLocalStorage
            }
        })
    }

    removeActiveUser = (removeId) => {

        let userStore = JSON.parse(localStorage.getItem('redux-store'));
        let activeUser = userStore.activeUsers;
        let deleteUserIndex = activeUser.findIndex(el => el.id === removeId);
         activeUser.splice(deleteUserIndex,1)
        userStore = {...userStore, activeUsers: [...activeUser]}
        localStorage.setItem('redux-store',JSON.stringify(userStore))

        this.setState((activeUsers) => {
            let activeUsersLocalStorage = JSON.parse(localStorage.getItem('redux-store')).activeUsers;
            return{
                activeUsers:activeUsersLocalStorage
            }
        })

    }


    render() {
        const {activeUsers} = this.state;

        const {users, isLoading, hasError} = this.props;


        if (isLoading) {
            return <Loader/>
        }
        if (hasError) {
            return <p>Error occurred!!!</p>
        }


        return (
            <div className='container'>
                <div className='Employee'>
                    <div className='Employee-left'>
                        <Employees
                            users={users}
                            addActiveUser={this.addActiveUser}
                            removeActiveUser={this.removeActiveUser}
                        />
                    </div>
                    <EmployeeBirthdayList activeUsers={activeUsers}/>
                </div>


            </div>
        )
    }
}

const mapStateToProps = (state) => ({
    users: state.users,
    isLoading: state.isLoading,
    hasError: state.hasError
})

const mapDispatchToProps = (dispatch) => ({
    loadUsers: () => dispatch(loadUsers()),
    // addActiveUser: (user) => dispatch(addActiveUser(user))
})

// const ADD_ACTIVE_USER = 'ADD_ACTIVE_USER';
//
// const addActiveUser = (user) =>{
//     return {
//         type: ADD_ACTIVE_USER,
//         payload: user
//     }
// }

App.defaultProps = {
    users: [],
}

App.protoType = {
    users: PropTypes.arrayOf(PropTypes.object),
    isLoading: PropTypes.bool.isRequired,
    hasError: PropTypes.bool.isRequired

}

export default connect(mapStateToProps, mapDispatchToProps)(App);
