import React from 'react';
import {connect} from "react-redux";
import Loader from "../Loader";
import {loadUsers} from "../UsersStore";
import PropTypes from 'prop-types';

import './app.css';
import EmployeeBirthdayList from "../EmployeerBirthdayList";
import Employees from "../Emoloyees/Employees";


function isInArray(id, arr) {
    return arr.some(e => e.id === id);
}


class App extends React.Component {

    state = {
        activeUsers: []
    }

    async componentDidMount() {
        await this.props.loadUsers();
    }

    addActiveUser = (obj) => {
        this.setState((prevState) => ({
            ...prevState,
            activeUsers: [
                ...prevState.activeUsers,
                obj
            ]
        }))
    }

    removeActiveUser = (removeId) => {
        this.setState(prevState => {

            if (!isInArray(removeId, prevState.activeUsers)) {
                return;
            }

            return {
                activeUsers: prevState.activeUsers.filter(user => user.id !== removeId)
            }
        })
    }


    render() {
        const {activeUsers} = this.state;
        const { isLoading, hasError} = this.props;

        if (isLoading) {
            return <Loader/>
        }
        if (hasError) {
            return <p>Error occurred!!!</p>
        }
        let users = localStorage.getItem('redux-store');
        console.log(users)

        return (
            <div className='container'>
                <div className='Employee'>
                    <div className='Employee-left'>
                        <Employees
                            users={users ? [] : users}
                            addActiveUser={this.addActiveUser}
                            removeActiveUser={this.removeActiveUser}
                        />
                    </div>
                    <EmployeeBirthdayList activeUsers={activeUsers}/>
                </div>


            </div>
        )
    }
}

const mapStateToProps = (state) => ({
    // users: state.users,
    isLoading: state.isLoading,
    hasError: state.hasError
})

const mapDispatchToProps = (dispatch) => ({
    loadUsers: () => dispatch(loadUsers()),
    // addActiveUser: (user) => dispatch(addActiveUser(user))
})

// const ADD_ACTIVE_USER = 'ADD_ACTIVE_USER';
//
// const addActiveUser = (user) =>{
//     return {
//         type: ADD_ACTIVE_USER,
//         payload: user
//     }
// }

App.defaultProps = {
    users: [],
}

App.protoType = {
    users: PropTypes.arrayOf(PropTypes.object),
    isLoading: PropTypes.bool.isRequired,
    hasError: PropTypes.bool.isRequired

}

export default connect(mapStateToProps, mapDispatchToProps)(App);
