import React, {useState, useRef} from "react";

import './User.css';


const User = ({firstName, lastName,addActiveUser}) => {
    const [isChecked, setIsChecked] = useState('active');
    const ref = useRef(null);


    const activeUser = () => {
        let current = ref.current;
        if (!current.classList.contains('active-text')) {
            return current.classList.add('active-text')
        }
        return current.classList.remove('active-text')
    }

    return (
        <div className='User'>
            <h3 className='User__title' ref={ref}>{`${firstName} ${lastName}`}</h3>

            <div className='User_radio'>
                <label>
                    <input type="radio"
                           name='activeBirth'
                           value='active'
                           checked={isChecked === 'active'}
                           onChange={(e) => {
                               setIsChecked(e.target.value);
                               activeUser();
                               addActiveUser({})
                           }}
                    />
                    not active
                </label>
                <label>
                    <input type="radio"
                           name='activeBirth'
                           value='notActive'
                           checked={isChecked === 'notActive'}
                           onChange={(e) => {
                               setIsChecked(e.target.value)
                               activeUser();
                           }}
                    />
                    active
                </label>
            </div>

        </div>
    )
}

export default User;
