import React, {useState, useRef, useContext} from "react";

import './User.css';


const User = ({user, addActiveUser, removeActiveUser}) => {
    // eslint-disable-next-line no-undef
    const activeUsers = useContext(activeUserForUser);
    console.log(activeUsers)
    const {firstName,lastName} = user;
    const [isChecked, setIsChecked] = useState(null);


    let valueForInput = Math.random();

    // const ref = useRef(null);
    // const activeUser = (target) => {
    //     let current = ref.current;
    //     if (!current.classList.contains('active-text') && target.value === 'active') {
    //         return current.classList.add('active-text')
    //     }
    //     return current.classList.remove('active-text')
    // }

    return (
        <div className='User'>
            <h3 className='User__title'
                style={{color: (isChecked) ? 'blue' : 'black'}}
            >{`${lastName} ${firstName}`}</h3>

            <div className='User_radio'>
                <label >
                    <input type="radio"
                           name={`BIRTH${valueForInput}`}
                           value='active'

                           onChange={(e) => {
                               setIsChecked(true);
                               addActiveUser(user)
                           }}
                           checked={isChecked === true}
                    />
                     active
                </label>
                <label>
                    <input type="radio"
                           name={`BIRTH${valueForInput}`}
                           value='notActive'

                           onChange={(e) => {
                               setIsChecked(false)
                               removeActiveUser(user.id)
                           }}
                           checked={isChecked === false}
                    />
                    not active
                </label>

            </div>

        </div>
    )
}

export default User;
