import React from 'react';
import {connect} from "react-redux";
import {getUsers} from "../data/data";
import Loader from "../Loader";
import {startLoading, handleSuccess,handleError} from "../UsersStore";


import './app.css';

class App extends React.Component{


    componentDidMount() {
       const {startLoading,handleSuccess,handleError} = this.props;

        this.setState({
            isLoading:true,
            hasError:false
        })

        // console.log(this.state.isLoading)

        getUsers()
            .then(users => {
            this.setState({
                users,
                isLoading:false
                })
            })
            .catch(() => {
                this.setState({hasError:true,isLoading:false})
            })

    }

    render() {
        const {users, isLoading,hasError} = this.props;


        if (isLoading){
            return <Loader/>
        }
        if (hasError){
            return <p>Error occurred!!!</p>
        }

        return(
            <div className='container'>

                <h1>Products</h1>
                {
                    users.map(el => (
                        <p key={el.id}>
                            {
                                el.firstName
                            }
                        </p>
                    ))
                }
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
        users: state.users,
        isLoading: state.isLoading,
        hasError: state.hasError
})

const mapDispatchToProps = (dispatch) => ({
    startLoading: () => dispatch(startLoading()),
    handleSuccess: (users) => dispatch(handleSuccess(users)),
    handleError: () => dispatch(handleError()),
})

export default connect(mapStateToProps,mapDispatchToProps)(App);
