import React from 'react';
import {getUsers} from "../data/data";
import Loader from "../Loader";


import './app.css';

class App extends React.Component{
    state ={
        users: [],
        isLoading: false
    }

    componentDidMount() {
        this.setState({isLoading:true})
        getUsers().then(users => {
            this.setState({
                users
            })
        })
    }

    render() {
        const {users, isLoading} = this.state;

        if (users.length === 0){
            return <p>No users yet</p>
        }

        if (isLoading){
           return <Loader/>
        }

        return(
            <div className='container'>

                <h1>Products</h1>
                {
                    users.map(el => (
                        <p key={el.id}>
                            {
                                el.firstName
                            }
                        </p>
                    ))
                }
            </div>
        )
    }
}
export default App;
