import React, {useState, useRef} from "react";

import './User.css';


const User = ({firstName, lastName,addActiveUser}) => {
    const [isChecked, setIsChecked] = useState('act');
    const ref = useRef(null);
    let user = `${firstName}${lastName}`;
    let labelForInputActive = Math.random();
    let labelForInputNotActive = Math.random();


    console.log(isChecked)
    const activeUser = () => {
        let current = ref.current;
        if (!current.classList.contains('active-text')) {
            return current.classList.add('active-text')
        }
        return current.classList.remove('active-text')
    }

    return (
        <div className='User'>
            <h3 className='User__title' ref={ref}>{`${firstName} ${lastName}`}</h3>

            <div className='User_radio'>
                <label htmlFor={labelForInputActive}>not active</label>
                <input
                    type="radio"
                    name='active'
                    id={labelForInputActive}
                    value='act'
                    checked={isChecked === 'act'}
                />

                <label htmlFor={labelForInputNotActive}>not active</label>
                <input
                    type="radio"
                    name='active'
                    id={labelForInputNotActive}
                    value='not-act'
                    checked={isChecked === 'not-act'}
                />

            </div>

        </div>
    )
}

export default User;

