import React from 'react';
import {getUsers} from "../data/data";
import Loader from "../Loader";


import './app.css';

class App extends React.Component{
    state ={
        users: [],
        isLoading: false,
        hasError: false
    }

    componentDidMount() {

        this.setState({
            isLoading:true
        })

        // console.log(this.state.isLoading)

        getUsers()
            .then(users => {
            this.setState({
                users,
                isLoading:false
                })
            })
            .catch(() => {
                this.setState({hasError:true,isLoading:false})
            })

    }

    render() {
        const {users, isLoading,hasError} = this.state;


        if (isLoading){
            return <Loader/>
        }
        if (hasError){
            return <p>Error occurred!!</p>
        }

        return(
            <div className='container'>

                <h1>Products</h1>
                {
                    users.map(el => (
                        <p key={el.id}>
                            {
                                el.firstName
                            }
                        </p>
                    ))
                }
            </div>
        )
    }
}
export default App;
