import React, {useState, useRef} from "react";

import './User.css';


const User = ({user}) => {
    const [isChecked, setIsChecked] = useState('act');
    const ref = useRef(null);
    // let user = `${firstName}${lastName}`;
    let valueForInput = Math.random();

    const activeUser = (target) => {
        let current = ref.current;
        if (!current.classList.contains('active-text') && target.value === 'active') {
            return current.classList.add('active-text')
        }
        return current.classList.remove('active-text')
    }

    return (
        <div className='User'>
            {/*<h3 className='User__title' ref={ref}>{`${firstName} ${lastName}`}</h3>*/}

            <div className='User_radio'>
                <label >
                    <input type="radio"
                           name={`BIRTH${valueForInput}`}
                           value='active'

                           onChange={(e) => {
                               setIsChecked(e.target.value);
                               activeUser(e.target);
                               addActiveUser({[user]: 'active'})
                           }}
                           checked={isChecked === 'active'}
                    />
                     active
                </label>
                <label>
                    <input type="radio"
                           name={`BIRTH${valueForInput}`}
                           value='notActive'

                           onChange={(e) => {
                               setIsChecked(e.target.value)
                               activeUser();
                           }}
                           checked={isChecked === 'notActive'}
                    />
                    not active
                </label>

            </div>

        </div>
    )
}

export default User;
